# Read the file
$filePath = "app\components\NumberPage.tsx"
$content = Get-Content $filePath -Raw -Encoding UTF8

# Apply translations
foreach ($num in $translations.Keys) {
    $oldPattern = "$num\: `"La tabla del $num.*?`","
    $newText = "$num: `"$($translations[$num])`","
    
    # Use regex to replace
    $content = $content -replace $oldPattern, $newText
}

# Save the file
Set-Content $filePath -Value $content -Encoding UTF8 -NoNewline
Write-Host "Translated entries 13-30" -ForegroundColor Green
